mapper.select:
select

from cskaoyan_mall_feedback


    username = #{username,jdbcType=INTEGER}


    and id = #{id,jdbcType=INTEGER}


order by ${sort} ${order}

mapper.insert:
insert into cskaoyan_mall_feedback (user_id, username,
  mobile, feed_type, content, 
  status, has_picture, pic_urls, 
  add_time, update_time, deleted
  )
values (#{userId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR},
  #{mobile,jdbcType=VARCHAR}, #{feedType,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, 
  #{status,jdbcType=INTEGER}, #{hasPicture,jdbcType=BIT}, #{picUrls,typeHandler=com.cskaoyan.mall.admin.typeHandler.StringToStingrArrayHandler,jdbcType=VARCHAR},
  #{addTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{deleted,jdbcType=BIT}
  )
