mapper.update:
update cskaoyan_mall_order
set order_status = 102
where id = #{orderId}

update cskaoyan_mall_order
set order_status = 202
where id = #{orderId}

update cskaoyan_mall_order
set order_status = 401
where id = #{orderId}

mapper.select:
select

from cskaoyan_mall_order
where deleted = 0 and user_id like "%"#{userId}"%" and order_sn like "%"#{orderSn}"%"

  and order_status in

    #{status}



  order by #{sort}

    #{order}
